import "./App.css";
import React, { Component } from "react";
import SearchPanel from "./components/SearchPanel";
import Info from "./components/Info";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      weather: null,
      isLoading: false,
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  url = "https://api.openweathermap.org/data/2.5/";

  key = "33178d46dea4c98a92d98aa6ea4ebc24";

  getData = () => {
    fetch(
      `${this.url}weather?q=${this.state.value}&appid=${this.key}&units=metric`
    )
      .then((response) => response.json())
      .then((response) =>
        this.setState({
          weather: response,
          isLoading: false,
        })
      );
  };

  startSearch = (e) => {
    if (e.key !== "Enter") {
      return;
    }
    this.setState({ isLoading: true, weather: null }, this.getData);
  };

  clearInput = () => {
    this.setState({
      value: "",
    });
  };
  check = () => {
    if (this.weather.name !== value) {
      return <p>Not exist city</p>;
    } else {
      return <Info weather={this.state.weather} />;
    }
  };

  render() {
    const { weather, value, isLoading } = this.state;
    return (
      <div className="App">
        <header>
          <h1>Weather today</h1>
        </header>
        <SearchPanel
          isLoading={isLoading}
          value={value}
          searchingCity={this.searchingCity}
          startSearch={this.startSearch}
          clearInput={this.clearInput}
        />
        {weather ? <div className="display">{this.check}</div> : null}
      </div>
    );
  }
}

export default App;
