import React from "react";
import { DateTime } from "luxon";

const Info = ({ weather }) => {
  return (
    <div className="info">
      <div className="city">
        {weather.name}, {weather.sys.country}
      </div>
      <div className="temperature">
        <p className="temp__feels_like">
          Feels like:{Math.round(weather.main.feels_like)}째C
        </p>
        <p className="temp_now">Now:{Math.round(weather.main.temp)}째C</p>
        <p>Max today: {Math.round(weather.main.temp_max)}째C</p>
        <p>Min today: {Math.round(weather.main.temp_min)}째C</p>
      </div>
      <div className="date">
        {DateTime.fromSeconds(weather.dt).toLocaleString(DateTime.DATE_HUGE)}
      </div>

      <div className="description">{weather.weather[0].main}</div>
    </div>
  );
};

export default Info;
