import "./App.css";
import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      weather: null,
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  url = "https://api.openweathermap.org/data/2.5/";

  key = "33178d46dea4c98a92d98aa6ea4ebc24";

  getData = () => {
    fetch(
      `${this.url}weather?q=${this.state.value}&appid=${this.key}&units=metric`
    )
      .then((response) => response.json())
      .then((response) =>
        this.setState({
          weather: response,
        })
      );
  };

  startSearch = (e) => {
    if (e.key !== "Enter") {
      return;
    }
    this.setState({}, this.getData);
  };

  render() {
    const { weather, value } = this.state;
    return (
      <div className="App">
        <header>
          <h1>Weather today</h1>
        </header>
        <div className="search">
          <input
            type="text"
            value={value}
            onChange={this.searchingCity}
            onKeyDown={this.startSearch}
          />
        </div>
        {weather ? (
          <div className="display">
            <div className="city">
              {weather.name}, {weather.sys.country}
            </div>
            <div className="temp">{weather.base}</div>
            <div className="info">{weather.weather[0].main}</div>
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
