import "./App.css";
import React, { Component } from "react";
import { DateTime } from "luxon";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      weather: null,
      isLoading: false,
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  url = "https://api.openweathermap.org/data/2.5/";

  key = "33178d46dea4c98a92d98aa6ea4ebc24";

  getData = () => {
    fetch(
      `${this.url}weather?q=${this.state.value}&appid=${this.key}&units=metric`
    )
      .then((response) => response.json())
      .then((response) =>
        this.setState({
          weather: response,
          isLoading: false,
        })
      );
  };

  startSearch = (e) => {
    if (e.key !== "Enter") {
      return;
    }

    this.setState({ isLoading: true }, this.getData);
  };

  render() {
    const { weather, value } = this.state;
    return (
      <div className="App">
        <header>
          <h1>Weather today</h1>
        </header>
        <div className="search">
          <input
            type="text"
            value={value}
            onChange={this.searchingCity}
            onKeyDown={this.startSearch}
            placeholder={"enter the city"}
            disabled={this.state.isLoading}
          />
          {this.state.isLoading ? 123 : 321}
        </div>
        {weather ? (
          <div className="display">
            <div className="city">
              {weather.name}, {weather.sys.country}
            </div>
            <div className="date">
              {DateTime.fromSeconds(weather.dt).toLocaleString(
                DateTime.DATE_HUGE
              )}
            </div>
            <div className="temp">
              <p>Temperature now:{Math.round(weather.main.temp)}°C</p>
              <p>
                Max today:
                {Math.round(weather.main.temp_max)}°C <br />
                Min today: {Math.round(weather.main.temp_min)}°C
              </p>
            </div>
            <div className="info">{weather.weather[0].main}</div>
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
