import "./App.css";
import React, { Component } from "react";
import SearchPanel from "./components/SearchPanel";
import Info from "./components/Info";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      weather: null,
      isLoading: false,
      existMassage: "",
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  url = "https://api.openweathermap.org/data/2.5/";

  key = "33178d46dea4c98a92d98aa6ea4ebc24";

  getData = () => {
    fetch(
      `${this.url}weather?q=${this.state.value}&appid=${this.key}&units=metric`
    )
      .then((res) => {
        if (res.status === 404) {
          const error = new Error();
          error.message = "You have to introduce a valid city name";
          throw error;
        }
      })
      .then((response) => response.json())
      .then((response) =>
        this.setState({
          weather: response,
          isLoading: false,
        })
      );

    if (this.state.weather !== this.state.value) {
      this.setState({
        existMessage: "Not Exist",
      });
    }
  };

  startSearch = (e) => {
    try {
      if (e.key !== "Enter") {
        return;
      }

      this.setState({ isLoading: true, weather: null }, this.getData);
    } catch (e) {
      console.log(e);
    }
  };

  clearInput = () => {
    this.setState({
      value: "",
      existMessage: "",
    });
  };

  render() {
    const { weather, value, isLoading, existMessage } = this.state;
    return (
      <div className="App">
        <header>
          <h1>Weather today</h1>
        </header>
        <SearchPanel
          isLoading={isLoading}
          value={value}
          searchingCity={this.searchingCity}
          startSearch={this.startSearch}
          clearInput={this.clearInput}
        />
        {weather ? (
          <div className="display">
            {weather.name !== value ? (
              <p>{existMessage}</p>
            ) : (
              <Info weather={weather} />
            )}
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
