import "./App.css";
import React, { Component } from "react";
import SearchPanel from "./components/SearchPanel";
import Info from "./components/Info";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
      weather: null,
      isLoading: false,
      existMassage: "",
      className: "",
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
      className: "",
      existMessage: "",
    });
  };

  url = "https://api.openweathermap.org/data/2.5/";

  key = "33178d46dea4c98a92d98aa6ea4ebc24";

  getData = () => {
    const { weather, value } = this.state;
    fetch(
      `${this.url}weather?q=${this.state.value}&appid=${this.key}&units=metric`
    )
      .then((response) => response.json())
      .then((response) =>
        this.setState({
          weather: response,
          isLoading: false,
          className: response.weather[0].main,
        })
      );
    if (weather !== value) {
      this.setState({
        existMessage: "City Not Found",
        className: null,
      });
    }
  };

  startSearch = (e) => {
    if (e.key !== "Enter") {
      return;
    }

    this.setState({ isLoading: true, weather: null }, this.getData);
  };

  clearInput = () => {
    this.setState({
      value: "",
      existMessage: "",
      className: "",
    });
  };

  render() {
    const { weather, value, isLoading, existMessage, className } = this.state;
    return (
      <div
        className={
          weather && weather.name === value ? `App ${className} ` : "App"
        }
      >
        <header>
          <h1>Weather today</h1>
        </header>
        <SearchPanel
          isLoading={isLoading}
          value={value}
          searchingCity={this.searchingCity}
          startSearch={this.startSearch}
          clearInput={this.clearInput}
        />
        {weather ? (
          <div className="display">
            {weather.name !== value ? (
              <p>{existMessage}</p>
            ) : (
              <Info weather={weather} />
            )}
          </div>
        ) : null}
      </div>
    );
  }
}

export default App;
