import "./App.css";
import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      value: "",
    };
  }

  searchingCity = (e) => {
    this.setState({
      value: e.target.value,
    });
  };

  getData = async () => {
    await fetch();
  };

  render() {
    return (
      <div className="App">
        <header>
          <h1>Weather today</h1>
        </header>
        <div className="search">
          <input
            type="text"
            value={this.state.value}
            onChange={this.searchingCity}
          />
        </div>
        <div className="display">
          <div className="city"></div>
          <div className="temp"></div>
          <div className="info"></div>
        </div>
      </div>
    );
  }
}

export default App;
